package com.vcampanholi.istiodevops.contract;import com.vcampanholi.istiodevops.model.PersonRequest;import com.vcampanholi.istiodevops.model.PersonResponse;import com.vcampanholi.istiodevops.service.PersonService;import lombok.AllArgsConstructor;import org.springframework.web.bind.annotation.*;import reactor.core.publisher.Flux;import reactor.core.publisher.Mono;@RestController@AllArgsConstructorpublic class PersonController {    private PersonService personService;    @PostMapping("/person")    public Mono<PersonResponse> createPerson(@RequestBody PersonRequest personRequest) {        return personService.createPerson(personRequest);    }    @GetMapping("/person")    public Flux<PersonResponse> findAllPerson() {        return personService.findAllPerson();    }    @GetMapping("/person/{id}")    public Mono<PersonResponse> findPersonById(@PathVariable("id") String id) {        return personService.findPersonById(id);    }    @PutMapping("/person/{id}")    public Mono<PersonResponse> updatePerson(@RequestBody PersonRequest personRequest,                                             @PathVariable("id") String id) {        return personService.updatePerson(personRequest, id);    }    @DeleteMapping("/person/{id}")    public Mono<Void> deletePerson(@PathVariable("id") String id) {        return personService.deletePerson(id);    }}