package com.vcampanholi.istiodevops.mapper;import com.vcampanholi.istiodevops.repository.PersonEntity;import com.vcampanholi.istiodevops.model.PersonRequest;import com.vcampanholi.istiodevops.model.PersonResponse;public class PersonMapper {    public static PersonResponse mapToResponse(PersonEntity personEntity) {        return PersonResponse.builder()                .id(personEntity.getId())                .name(personEntity.getName())                .age(personEntity.getAge())                .build();    }    public static PersonEntity mapToEntity(PersonRequest personRequest) {        return PersonEntity.builder()                .id(personRequest.getId())                .name(personRequest.getName())                .age(personRequest.getAge())                .build();    }}